import pytest@pytest.mark.parametrize("HDL_input, expected",                         [(85, "Normal"),                          (45, "Borderline Low"),                          (20, "Low")])def test_check_HDL(HDL_input, expected):    from blood_calculator import check_HDL    answer = check_HDL(HDL_input)    assert answer == expected@pytest.mark.parametrize("LDL_input, expected",                         [(120, "Normal"),                          (140, "Borderline High"),                          (180, "High"),                          (190, "Very High")])def test_check_LDL(LDL_input, expected):    from blood_calculator import check_LDL    answer = check_LDL(LDL_input)    assert answer == expected@pytest.mark.parametrize("cholestrol_input, expected",                         [(180, "Normal"),                          (220, "Borderline High"),                          (250, "High")])def test_check_total_cholestrol(cholestrol_input, expected):    from blood_calculator import check_total_cholestrol    answer = check_total_cholestrol(cholestrol_input)    assert answer == expected